import React from "react";
import CountUp from "react-countup";

const Card = ({ data }) => {
    return (
        <div className="w-full bg-white flex justify-between p-6">
            <div className="flex flex-col justify-between">
                <div className="text-[#A8A8A8] text-[24px] font-[400]">{data.title}</div>
                <div>
                    {/* <div className="text-[48px] font-[500] text-[#555555]">{num.toLocaleString("en-US", { style: "currency", currency: "USD" })}</div> */}
                    <div className="text-[48px] font-[500] text-[#555555]">
                        <CountUp
                            end={data.amount}
                            duration={2.75}
                            separator=","
                            decimals={2}
                            decimal="."
                            prefix="$"
                        />
                    </div>
                    <div className="flex items-center">
                        <ArrowUp />
                        <span className="text-[#0095FF]">{data.growth}%</span>
                        <span className="text-[#999999] ml-2">VS. Previous month</span>
                    </div>
                </div>
            </div>
            <div className="h-full flex items-center">
                {data.icon === "sales" ? <SalesSvg /> : <ProfitsSvg />}
            </div>
        </div>
    );
};

export default Card;

